base_css:
body { font-family: 'Segoe UI', Arial, sans-serif; background: #f5f8fa; color: #222; margin: 0; padding: 0; line-height: 1.7; }
header { background: #0a2239; color: #fff; padding: 24px 0 16px 0; text-align: center; }
nav { background: #fff; border-bottom: 1px solid #e0e0e0; padding: 0 0 0 0; text-align: center; }
nav a { color: #0a2239; text-decoration: none; margin: 0 18px; font-weight: 500; font-size: 1.08em; transition: color 0.2s; }
nav a:hover { color: #1e90ff; }
.hero-image { width: 100%; height: 320px; background: linear-gradient(120deg, #1e90ff 0%, #0a2239 100%); display: flex; align-items: center; justify-content: center; color: #fff; font-size: 2.2em; font-weight: 700; letter-spacing: 1px; }
main { max-width: 1120px; margin: 32px auto; background: #fff; border-radius: 12px; box-shadow: 0 2px 16px rgba(30,144,255,0.06); padding: 36px 24px; }
section { margin-bottom: 44px; }
h1 { font-size: 2.4em; color: #0a2239; margin-bottom: 18px; font-weight: 700; }
h2 { font-size: 1.6em; color: #1e90ff; margin-top: 36px; margin-bottom: 14px; font-weight: 600; }
h3 { font-size: 1.18em; color: #0a2239; margin-top: 22px; margin-bottom: 10px; font-weight: 500; }
p { margin-bottom: 18px; }
a { color: #1e90ff; text-decoration: underline; transition: color 0.2s; }
a:hover { color: #0a2239; }
button, .btn { background: #1e90ff; color: #fff; border: none; border-radius: 5px; padding: 12px 28px; font-size: 1em; font-weight: 600; cursor: pointer; transition: background 0.2s; margin-top: 12px; }
button:hover, .btn:hover { background: #0a2239; }
footer { background: #0a2239; color: #fff; text-align: center; padding: 26px 0 16px 0; margin-top: 40px; font-size: 1em; }
@media (max-width: 800px) { main { padding: 18px 6px; } .hero-image { height: 160px; font-size: 1.2em; } h1 { font-size: 1.5em; } h2 { font-size: 1.18em; } }
